INSERT INTO `wp_options` VALUES ('223', 'bpm_products', 'a:4:{i:0;a:17:{s:16:\"product_web_slug\";s:44:\"https://bpmcontext.com/product/intranet-pro/\";s:14:\"product_web_id\";s:3:\"372\";s:10:\"product_id\";s:1:\"2\";s:12:\"product_name\";s:12:\"Intranet Pro\";s:13:\"product_image\";s:12:\"fa-dashboard\";s:10:\"short_desc\";s:128:\"<p>Expand your Intranet with support ticket management, competitor tracking and department level communications workspaces.</p>\n\";s:9:\"prod_type\";s:16:\"Workspace Suites\";s:11:\"prod_status\";s:1:\"1\";s:10:\"prod_price\";s:6:\"120.00\";s:9:\"prod_desc\";s:12:\"Intranet Pro\";s:11:\"bill_period\";s:6:\"Annual\";s:7:\"site_id\";s:1:\"5\";s:14:\"storage_amount\";s:1:\"0\";s:16:\"limit_to_context\";s:1:\"0\";s:9:\"is_wf_sub\";s:1:\"0\";s:16:\"installs_allowed\";s:1:\"0\";s:9:\"can_trial\";s:1:\"0\";}i:1;a:17:{s:16:\"product_web_slug\";s:50:\"https://bpmcontext.com/product/client-suites-plus/\";s:14:\"product_web_id\";s:3:\"421\";s:10:\"product_id\";s:1:\"5\";s:12:\"product_name\";s:18:\"Client Suites Plus\";s:13:\"product_image\";s:8:\"fa-group\";s:10:\"short_desc\";s:190:\"<p>Client Suites Plus delivers workspace suites for managing customer information and contacts. Designed to organize contacts and information by office locations such as branch offices.</p>\n\";s:9:\"prod_type\";s:16:\"Workspace Suites\";s:11:\"prod_status\";s:1:\"1\";s:10:\"prod_price\";s:1:\"0\";s:9:\"prod_desc\";s:18:\"Client Suites Plus\";s:11:\"bill_period\";s:6:\"Annual\";s:7:\"site_id\";s:1:\"6\";s:14:\"storage_amount\";s:1:\"0\";s:16:\"limit_to_context\";s:1:\"0\";s:9:\"is_wf_sub\";s:1:\"0\";s:16:\"installs_allowed\";s:3:\"100\";s:8:\"accounts\";a:1:{i:0;a:1:{s:10:\"account_id\";s:4:\"8450\";}}}i:2;a:16:{s:16:\"product_web_slug\";s:45:\"https://bpmcontext.com/product/intranet-plus/\";s:14:\"product_web_id\";s:3:\"405\";s:10:\"product_id\";s:1:\"7\";s:12:\"product_name\";s:13:\"Intranet Plus\";s:13:\"product_image\";s:12:\"fa-dashboard\";s:10:\"short_desc\";s